<?phpsession_start();$conn=new mysqli("localhost","root","","g10_db");if($conn->connect_error){	die("error to connect: ".$conn->connect_error);}if(isset($_POST['submit'])){	$email=mysqli_real_escape_string($conn,$_POST['email']);	$pass=mysqli_real_escape_string($conn,$_POST['password']);	$sql="select * from `form` where email='$email' and password='$pass'";$run_login=mysqli_query($conn,$sql);$result=mysqli_fetch_array($run_login);echo $result['name'];$username=$result['name'];$check_login=mysqli_num_rows($run_login);if($check_login==1){	//setcookie('username',$username);	$_SESSION['username']=$username;	//echo "login successful";	header('location:timeline.php');}	else	{		header('location:incorrect.html');	}}$conn->close();?>